insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3848,0,50,7,12,'Kayneth Stillwind','Uh... the disease is potent.  And it attacks the mind...','I must... RESIST!','I have survived the poisons of the Forsaken, $N, and I know now what must be done.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3849,0,50,1,12,'Deathstalker Adamant','Free from this wretched cell at last! Let me show you to the courtyard....','You are indeed courageous for wanting to brave the horrors that lie beyond this door.','There we go!','Good luck with Arugal. I must hurry back to Hadrec now.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3850,0,50,7,12,'Sorcerer Ashcrombe','Follow me and I\'ll open the courtyard door for you.','I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly.','There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3892,0,50,7,12,'Relara Whitemoon','Uh... Father...?  Are you there?  I\'m hungry... can I have some stew?','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3894,0,50,7,12,'Pelturas Whitemoon','Here you are, Relara.  Take this...','What?','You\'re awake??  Your cure must have worked!  Thank you, $N.','Eat this, my daughter.  It will help you feel better.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (3916,0,50,7,12,'Shael\'dryn','The rod\'s design is simple. Dartol didn\'t use his arcane knowledge in its creation... it is more druidic in nature.','Hehe... I made a pun, didn\'t I? Druidic in nature... I\'m so funny.','Now, then... the rod. Let\'s see about fixing it, shall we?','There! Now it just needs to be recharged...','');
