insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16481,5,100,0,12,'Ghastly Haunt','Free... at last.','Time to move on...','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16482,5,100,0,12,'Trapped Soul','Free... at last.','Time to move on...','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16483,0,50,35,12,'Draenei Survivor','$C, you saved me!  I owe you a debt that I can never repay.  I\'ll go see if I can help the others.','The last thing I remember is the ship falling and us getting into the pods.  I\'ll go see how I can help.  Thank you!','Where am I?  Who are you?  Oh no!  What happened to the ship?','Ugh... what is this place?  Is that all that\'s left of the ship over there?','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16485,5,100,0,12,'Arcane Watchman','Others will take my place!','This... changes nothing. Eternal damnation awaits you!','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16500,0,50,7,12,'Valaatu','No...','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16501,0,50,7,12,'Aurelon','As you wish.','As the Light wills.','Of course, Zalduun.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16502,0,50,7,12,'Zalduun','I could use some help over here if you please.','Paladin Aurelon, if you have the mana to spare, could you help with that one, please?  Thank you.','Oh no!  I\'m losing this one!','Paladin, perhaps you would lend a hand?  I just need another heal on that one.  Thank you.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16514,0,50,0,12,'Botanist Taerix','Tedon, go ahead and try the neutralizing agent on the mutated creature.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16521,0,15,0,12,'Blood Elf Scout','What a pity you survived the crash just to die here.','We won\'t allow you to leave this valley!','I hope you\'ve made your peace.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16522,0,100,0,12,'Surveyor Candress','You\'ll not take me alive, scum!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16544,5,100,0,12,'Ethereal Thief','You will succeed only in damning yourselves!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16550,0,50,7,12,'Image of Technician Dyvuun','By the seven Ata\'mal crystals!  Others have survived the crash!  Zhanaa, is that you?  It is so good to see you again!','And who do we have here?  No doubt th... crzzzk ...ade this communication possible?  I give you greetings fro... psshzzzk... Azure Watch.','Judging by your signal, we put you... ust southeast of the larger part of the island we crashed onto.  Cross the Crystalbrook River to ge... kshhhhk zzzt. Sorry, there\'s a lot of stat...','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16551,0,50,7,12,'Technician Dyvuun','Yes, Zhanaa, it\'s me!  Your signal is weak.','Zhanaa!  Gah, I have to get this thing fixed!','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16576,0,50,1,12,'Overlord Hun Maimfist','For the Warchief! For the Horde!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16577,0,50,1,12,'Martik Tor\'seldori','Brothers and sisters, I have been to the promised land. I have tasted in the sublime energy. I have felt bliss - bliss so engrossing and all encompassing that I was left wondering if I had stumbled upon the dreams of gods.','In paradise, you merely reach out and take from the Nether.','From the very air! Tendrils of arcane energy light up the obsidian sky as plumes of raw magic rise up from fissures in the land. Kneel and drink from the fissure as you do from a stream or well. Pluck a tendril from the heavens as if it were an apple hang','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16578,0,50,1,12,'Blood Elf Pilgrim','Tell us more, Martik. What is it? What does it feel like?','But how?','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16580,0,50,1,12,'Thrallmar Grunt','Try it, and I\'ll fire you AND your ten silver over that wall.','You cut the rope. You have the knife.','Harumph! Coward.','You know, I think we can hit Honor Hold from here.','I say we fire it. Cut the rope.');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16584,0,50,1,12,'Watch Commander Krunk','Don\'t say I didn\'t warn ya when I thrust my fist into the back of yer head. It\'ll feel just like home, back in the Valley of Trials.','Yer gonna learn why they call this place Hellfire, maggot.','This ain\'t daycare, peon. Double time!','I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16585,0,50,1,12,'Cookie One-Eye','And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16587,0,100,1,12,'Barley','Arrrhhh...Guards!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16589,0,50,1,12,'Guard Captain Cragtar','Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here... ','So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16590,0,50,1,12,'Injured Thrallmar Grunt','It tastes like I died and went to the Nether; where slop that tastes like rotting threshadon carcass is then shoved down my throat by an obese tauren that\'s never washed their hands - or bathed...','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16593,0,50,0,12,'Shattered Hand Brawler','This world is OURS!','The blood is our power!','Lok\'tar Illadari!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16594,0,50,0,12,'Shadowmoon Acolyte','Lok\'tar Illadari!','The blood is our power!','This world is OURS!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16700,0,50,0,12,'Shattered Hand Legionnaire','We will destroy you.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16769,0,50,0,12,'Firewing Warlock','Kill them before they get away!','You won\'t escape alive!','For Kael\'thas!','They mustn\'t escape!','You know too much!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16790,0,50,0,12,'Falconer Drenna Riverwind','Thank you, $N.  It looks like they\'re going to get along just fine.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16793,0,50,1,12,'Magistrix Carinda','You\'re such a good friend, Twinkle.  You\'d never leave me, would you?','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16794,0,50,0,12,'Apothecary Azethen','...a potent concoction!','Hmmm... looks like I\'m going to need a new test subject.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16795,0,50,35,12,'Draenei Prisoner','I pity you, Forsaken.  Your soul is devoid of light.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16799,0,50,0,12,'Ikan','Welcome, my brethren!  You\'ve been freed from demons.  Embrace the teachings of Velen!  Embrace the light!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16819,0,50,7,12,'Force Commander Danath Trollbane','As I live and breathe! \"Little\" Arator? Is that you?','I am afraid that I do not, Arator. I have not seen or heard from your father in 15 years. We had long since presumed him dead. But know this: I did all that was in my power to try and find him. From the bounds of Hellfire to the ravages of Nagrand, we sea','That is precisely why I have called for you old friend. We have much to discuss.','Welcome to Honor Hold, $N.  It\'s good to have you.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16821,0,50,7,12,'Magus Filinthus','And have you heard? Have you heard the preposterous claims? Alliance and Horde supporting peace? Why the very thought of such things makes my blood boil!','Twenty years lost! Twenty! I won\'t stand for this! No, no, no... ','I take my leave now, Commander. Good day!','A mailbox? Well is that not the salt on the wounds! Twenty years cut-off from the world and now a mailbox! WONDERFUL!','Sid! Ale, NOW!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16822,0,100,7,12,'Flightmaster Krill Bitterhue','Guards!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16825,0,50,7,12,'Father Malgor Devidicus','Shay, what\'sh your deal anwaysh? Are you shome kind of elf or shomethin? You looksh shorta familiar.','By the Light! So this is why you look familiar. Is this fate? Destiny perhaps? Arator, your father and I were the best of friends. It is I who kept him in good health... Oh the battles we fought... And...','And... the battle we lost. I... I\'m sorry, Arator. It was my fault...','Quickly, Sid. Fill my tankard. Make the memories fade...','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16826,0,50,7,12,'Sid Limbardi','Oh brother... Listen, he\'s gonna be here crying like a baby for another five hours. If you\'re really the son of Turalyon, go speak with Trollbane. He\'s in the keep - top floor.','Just leave \'em in the cellar.','Comin\' right up, yer highness! Is there anythin\' else you\'ll be needin\'? Perhaps yer hat cleaned or yer nails trimmed?','Bless yer heart, soldier! Just put \'em in the back room.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16830,0,50,7,12,'Field Commander Romus','At ease, Private. I have a mission for you. The last messenger I sent to Nethergarde didn\'t make it. We found his entrails scattered about the bone road a few hours ago. We suspect it may have been Legion or fel orc, maybe even Horde...','I need this message delivered to Watch Commander Netherwane at Nethergarde Keep. There\'s a commendation in it for you if you make it... Your horse is waiting at the stables. Dismissed!','Soldier! Front and center!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16831,0,50,7,12,'Nethergarde Infantry','SIR! YES, SIR! Reporting for duty, sir!','Got another shipment for you, Sid.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16833,0,50,0,12,'Makuru','No!  Not... Sedai!  The orcs must pay!','The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16834,0,50,0,12,'Anchorite Obadei','What have you done, Makuru?!  These are not our ways!','I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16842,0,50,7,12,'Honor Hold Defender','Do ya want me to call a medic? Maybe a priest? It\'s been over an hour, mate!','What\'s goin\' on in there?? For the love of the LIGHT, hurry it up!','OY! You in there, this is official Honor Hold business yer holdin\' up!','So help me, I\'m gonna count to ten and if yer not outta there by the time I reach 10, I\'m comin\' in!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16851,0,50,0,12,'Mirren Longbeard','You treat my hat well now, $N.  You won\'t find a finer one anywhere in this world or another.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16864,0,50,7,12,'Stormwind Infantry','Got another shipment for you, Sid.','Sir, yes sir! Private will do their best to not die, sir!','Where do you want these bottles?','Craziest thing - No matter how many bottles I take from the stockpile, the quantity on hand never decreases. It\'s like I\'m stuck in some endless loop of actions...','SIR! YES, SIR! Reporting for duty, sir!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16873,0,15,0,12,'Bleeding Hollow Dark Shaman','The Brotherhood will not tolerate your actions.','The Brotherhood will not tolerate your actions.','Feel the power of the Brotherhood!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16886,0,50,7,12,'Arator the Redeemer','Aye, Commander; but I am little no more. I have come to find my father. Have you any news as to his whereabouts? I know that he still lives. I have seen it in my dreams...','As long as he remains unfound, I will search. I know that he lives. And I know that I must find him. I am thankful for what you have done, Commander. As my father called upon you, a brother in arms, know that you may call upon me. I am at your service, my','Farewell, Commander. I will be staying at the inn, should you require my services.','I am Arator, son of Turalyon and Alleria. So yes, Father, I am \"shome\" kind of elf: half-elf to be precise.','Take care of him, Sid. He may be the only link I have to my father and mother. He needs to sober up...');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16907,0,50,0,12,'Bleeding Hollow Peon','For Spinebreaker!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16916,0,50,1,12,'Wretched Captive','Please!  I must feed on something soon... A mana crystal.... a shard... anything!  The pain is unbearable!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16923,0,50,1,12,'Novice Ranger','I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16924,0,50,1,12,'Sergeant Kan\'ren','You\'re not getting anything until you speak, scum!','Tell us!  Where does your leader hide?','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16978,0,50,0,12,'Lieutenant Commander Thalvos','Everybody settle down! We\'re doing our best to get the situation under control. Them bleedin\' orcs are unrelenting!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (16993,0,50,0,12,'Wounded Blood Elf Pilgrim','Thank you for agreeing to help. Now, let\'s get out of here, $N!','You won\'t keep me from getting to Falcon Watch!','Over there! They\'re following us!','Allow me a moment to rest. The journey taxes what little strength I have.','Do you hear something?');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17000,0,50,0,12,'Aggonis','Fools!  You seek to abjure my father\'s presence?  Death awaits you, mortals!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17008,0,50,0,12,'Gul\'dan','Be silent! The shattering is soon to come!','Bear witness to the undeniable power of our dark master!','With his gift, I shall raze this land and reform it!','Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies...','We will never be without power! I have secured our future! Bask in my glory!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17058,0,50,0,12,'Illidari Taskmaster','You dare interfere with the Master\'s endeavors?  You will pay, foolish mortal!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17071,0,50,7,12,'Technician Zhanaa','This is great news!  Your efforts have indeed saved us, $N!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17083,0,50,0,12,'Fel Orc Convert','Lok\'tar Illadari!','The blood is our power!','This world is OURS!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17093,0,50,0,12,'Magistrix Elosai','I\'ve endured much hardship to complete my research and with your help, $N, I\'ve finally done it. Here\'s to a brighter future!','I feel... lightheaded... What\'s... happening to me?','No, this can\'t be! This tonic... it was supposed to reverse my addiction, not hasten my descent into the ranks of the Wretched!','All those years of research... and for nothing! Perhaps they were right, perhaps this is something we must learn to live with.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17109,0,50,0,12,'Cersei Dusksinger','Oh, would you please stop checking your nails and spanking yourself, for one moment?  Really, no one cares!','I need some fresh air, not that there\'s any to be had in this swamp!  I\'m going out for a bit.  Tidy up a bit while I\'m gone, would you?','Must you follow me everywhere? <sigh>','Are you hungry?  I\'m hungry.  I just can\'t stand the thought of cured ham steak from Thultash again.  If only we could get some of that succulent roasted quail!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17117,0,50,7,12,'Injured Night Elf Priestess','Whe... Where am I? What... What is this place?','BY THE LIGHT OF ELUNE! Eredar! Cursed demons, what have you done to me? I have not come alone, we will fight you as we have in the past! I...','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17123,0,50,1,12,'Earthcaller Ryga','Welcome, kind spirit.  What has brought you to us?','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17127,0,50,0,12,'Anchorite Avuun','I must find a way to put an end to the evil that Cersei Dusksinger is committing against our lost brothers and sisters!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17206,0,50,0,12,'Hauteur','You dare summon me?!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17209,0,100,7,12,'William Kielar','Guards!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17214,0,50,7,12,'Anchorite Fateema','Now to apply the ointment to her wounds.','It appears that the ointment is ineffective. Such a pity...','Man\'ari... But how... We are not eredar! She must know! If there are others like her, we must find them and explain...','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17215,0,50,7,12,'Daedal','There may still be a solution. Come here, $N. I will explain what I need.','This should do it...','Wake up little creature. Wake up from your slumber... You are safe now.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17220,0,50,0,12,'Hauteur Reborn','Thank you, shaman.  I am reborn and will burn brighter than ever before!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17222,0,50,7,12,'Artificer Daelo','This won\'t do at all! We have to secure the foundation or the entire structure will crumble!','This structure violates at least 8 different ordinances!','I won\'t be held responsible for the catastrophe that will surely result from the haphazard maintenance of this building.\r\n','This won\'t work at all! The buildings will surely collapse if we do not shore up the support beams.','Hrm, this looks much better. Let\'s have a look on the inside.');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (17226,0,50,0,12,'Viera Sunwhisper','Ok, let\'s go.  I know just the perfect spot!','What... is happening... to me?  Get this cat away from me!','','','');
