insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (18999,0,50,7,12,'Allerian Defender','Hey, did you read the bounty on this wanted poster?','I don\'t know and I don\'t care.','The bonelashers of the wastes are no laughing matter.  I hear they carry diseases!','This wanted poster says that they\'re offering gold to these so-called adventurers to go kill things.  Isn\'t that what we do?  I don\'t think I\'m being paid enough!','This wanted poster has a bounty on bonelashers.  You think I should do that?');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19035,0,50,0,12,'Wazat','If only I could get this blasted machine working!','They look so delicious...','Oh sweet, delicious, spotted eggs - you will be mine... ','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19138,0,50,0,12,'Anchorite Attendant','May it be so.','','','','');
