insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19149,0,50,7,12,'Telaari Citizen','How does he do it?','He\'s so well disciplined!','I wanna be just like the pitfighter!','The pitfighter will be victorious! Just look at him!','');
