insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19193,0,50,0,12,'High Exarch Commodus','On the contrary, my friend.','With the intelligence you\'ve provided on the enemy and their tactics we\'ve gained a significant edge in the field.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19202,0,50,0,12,'Emissary Mordin','You give me too much credit, good sir. I regret we cannot be of more assistance in such perilous times.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19216,0,50,0,12,'Grand Anchorite Almonen','Sadly, there are those who wander through mortality in defiance of the Light.','It has been made known to me that inside each of us, the Light resides...','Simple kindnesses, charitable deeds, service to those in need. These are all fruits of the Light.','It rewards those who heed its promptings with blessings, both seen and unseen.','From these the Light withdraws, until only darkness remains.');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19218,0,100,0,12,'Gatewatcher Gyro-Kill','I predict a painful death.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (19218,5,100,0,12,'Gatewatcher Gyro-Kill','An unforeseen... contingency.','','','','');
