insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20763,0,50,0,12,'Captured Protectorate Vanguard','For the Protectorate! DIE ETHEREUM FILTH!','We\'re not too far from the Protectorate Watch Post, $N. This way!','Commander! This fleshling rescued me!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20765,0,50,0,12,'Bladespire Crusher','I\'ll crush you!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20780,0,50,0,12,'Kaylaan','Do not die on me, vindicator!','I\'ve failed.  The Light has abandoned me.','Light!  Do not fail me!','It is hopeless... I\'ve done all I can.','Kael\'thas and the Legion... just what we needed!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20794,0,50,0,12,'Kaylaan the Lost','Yes... master.','Teacher...','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20802,0,50,0,12,'Protectorate Demolitionist','Let\'s do this... Just keep me covered and I\'ll deliver the package.','With this much void waste and run-off, a toxic void horror can\'t be too far behind.','By the second sun of K\'aresh, look at this place! I can only imagine what Salhadaar is planning. Come on, let\'s keep going.','Look there, fleshling! Salhadaar\'s conduits! He\'s keeping well fed....','Alright, keep me protected while I plant this disruptor. This shouldn\'t take very long.');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20812,0,50,0,12,'Corki','Congratulations. It smells worse in here than in the ruins.','This is the last time I get caught! I promise! Bye!','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20825,0,50,0,12,'Captain Tyralius','You\'ve saved me, fleshling! Ameer will hear of this noble act.','','','','');
