insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20913,0,50,0,12,'Tashar','Now to begin the the repairs.','It\'s working! Ghabar will pleased to hear the news.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (20985,0,50,0,12,'Captain Saeed','You heard the fleshling! MOVE OUT!','Tell me when you are ready, $N. We will attack on your command.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21024,0,50,0,12,'Earthmender Torlok','Stand back, $N. The spirits may lash out.','We wish to help you, spirit. We seek your wisdom.','You are right, spirit. It was my people that did this to you. Allow us to make it right.','Please tell us this: Who or what is Cyrukh?','The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21049,0,50,0,12,'Spirit of the Past','You have damned us all... We are lost.','Tear the land asunder, Gul\'dan! Sever the tie!','Do not do this, Gul\'dan! The elements will never forgive us!','Think of our younglings, Gul\'dan! You will destroy us all!','They have abandoned us! Do not forget!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21107,0,100,7,12,'Rip Pedalslam','Guards!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21129,0,50,0,12,'Scorned Spirit of Earth','Why have you captured us?','Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.','Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master.','Talk. More talk... Now release us, mortal.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21130,0,50,0,12,'Scorned Spirit of Fire','What is it that you hope to gain from this?','He is insane now. Driven mad during the shattering. There is no rest for the elements.','Yes... Gul\'dan...','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21131,0,50,0,12,'Scorned Spirit of Water','Wisdom? It was your people that caused the insufferable wrath of Cyrukh.','Yes, a way. Destroy Cyrukh. Destroy an elemental lord.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21132,0,50,0,12,'Scorned Spirit of Air','Why should we help you? The world is ruined. Destroyed.','You must utter the words...','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21133,0,50,0,12,'Corporal Ironridge','This insignia is from Sergeant Rantack, once my superior.  You\'d never find a tougher soldier than old Rantack...','Damn you!  Damn all of you bleeding fel orcs!  May your souls rot in the belly of whatever Dark Power that fuels you!','This one was from Corporal Stonewell... he died protecting a field hospital...','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21134,0,50,0,12,'Eye of the Citadel','How dare you trifle with my playthings, $n!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21136,0,50,0,12,'Infinite Chronomancer','We will not fail!','A minor victory...','You will not stand in our way!','We are not finished!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21137,0,50,0,12,'Infinite Assassin','We will not be stopped!','More will take my place.','Your efforts... are in vain.','Enforcers! Kill them all!','You will not save him!');
