insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21188,0,50,1,12,'Thadok','Excellent news, Tooki! We\'re saved!','Listen up, you lot! Our worries our over! FREE BOOZE FOR EVERYONE!','','','');
