insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21400,0,50,7,12,'Eckert','We\'re saved? We\'re really saved?! YAHOO!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21402,0,50,0,12,'Anchorite Ceyla','Light be with you, Harbinger.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21409,0,50,0,12,'Envoy Icarius','Their loss is most definitely my gain...','Ah, damn it all! Clever trap. Too bad you\'ll never live to tell the tale...','Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed!','Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21410,0,50,0,12,'Blood Lord Zarath','What is it, my lord?','My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.','As you wish, my lord.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21411,0,50,0,12,'Tobias the Filth Gorger','That was life-changing... Excuse me for one minute.','Ok then, back to business.','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21434,0,50,0,12,'Gordie','It\'s a complete and total barf-o-rama! RUN FOR YOUR LIVES!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21453,0,50,0,12,'Ashtongue Shaman','Let me know right away if you find something important.','Dig! Dig for the glory of the master!','Keep up the good work.  Do well and you will be rewarded by the master.','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21456,0,15,0,12,'Ogre Building Bunny Small','i kill you $N','I\'ll crush you!','I will trample you $N','ah you is pitiful cockroach!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21469,0,50,0,12,'Daranelle','Good, $n, you are under the spell\'s influence. I must analyze it quickly, then we can talk.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21496,0,50,0,12,'Dertrok','Cansis, hold still. I\'m going to try to use this crystal.','Interesting... Let me try something.','Cansis. You are a chicken.','Fascinating.','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21500,0,50,0,12,'Morgroron','We will conquer all, for we are the Burning Legion!  Our enemies shall be consumed in flame and crushed beneath our might!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21501,0,50,0,12,'Makazradon','None can stand against the Burning Legion!  This world is ours!','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21502,0,50,0,12,'Image of Warbringer Razuun','Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste.','Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan.','Yes?','Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them!','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21504,0,50,0,12,'Pathaleon the Calculator\'s Image','And just what do you think you are doing?  You dare to interfere with my master\'s experiment?','Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland.','I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal!','I believe I may recognize them.  Deal with this quickly, Sharth.  Then take the mana bomb and destroy their town!','Indeed, it is not a request.');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21633,0,50,0,12,'Deathbringer Jovaan','Everything is in readiness, warbringer.','Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulernable to a counterattack.','It shall be as you say, warbringer. One last question, if I may...','What\'s in the crate?','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21648,0,50,11,12,'Mature Netherwing Drake','Thank you, mortal.','','','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21660,0,50,0,12,'Cabal Abjurist','I do as I must!','Ruin finds us all!','The end comes for you!','','');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21661,0,50,0,12,'Cabal Skirmisher','Ruin finds us all!','I do as I must!','I shall be rewarded!','The end comes for you!','The Legion reigns!');
insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21662,0,50,0,12,'Cabal Tomb-Raider','The Legion reigns!','I shall be rewarded!','The end comes for you!','Ruin finds us all!','');
