insert  into `npc_monstersay`(`entry`,`event`,`chance`,`language`,`type`,`monstername`,`text0`,`text1`,`text2`,`text3`,`text4`) values (21699,0,50,0,12,'Maiev Shadowsong','Release me immediately or face my wrath!','If we truly desire the same thing, Akama, then release me!  If Illidan is to die, it shall be by my hand!','Curse you, Akama!  I am not a pawn in your game... my will is my own.  When I unleash my wrath upon Illidan it will have nothing to do with your foolish scheme!','','');
